import React from 'react'
import type { ComponentType } from 'react'

interface BlockProps {
  content: string
  style?: React.CSSProperties
}

const Block: ComponentType<BlockProps> = ({ content, style }) => {
  return (
    <div style={style}>
      {content}
    </div>
  )
}

export default Block

